    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        
        Random rand = new Random(); // Instancia de la clase Random
        String imagen, auto;
        String autos[] = {"", "Ferrari california", "Ferrari", "Ford Maverick", "Isuzu MU", "Isuzu", "Jetta tunning", 
            "Maserati mc20", "Nissan Frontier", "Volkswagen jetta de carreras", "Volkswagen Jetta"}; // Creamos un arreglo con las posibles soluciones de autos
        
        int posicion = 1 + rand.nextInt((autos.length-1)); // Obtenemos una posicion del arreglo de manera aleatoria
        auto = autos[posicion]; 
        imagen = "/img/" + auto + ".jpg"; // Creamos el nombre de la imagen a mostrar
        
        // Las siguientes lineas son para hacer que las imagenes se ajusten de tama√±o y no se muestren tan grandes
        ImageIcon imgIcon = new ImageIcon(getClass().getResource(imagen));
        Image imgEscalada = imgIcon.getImage().getScaledInstance(jLabel1.getWidth(), jLabel1.getHeight(), Image.SCALE_SMOOTH);
        Icon iconoEscalado = new ImageIcon(imgEscalada);
        jLabel1.setIcon(iconoEscalado);
        
        System.out.println("Felicidades, usted ha ganado un: " + auto);
    }//GEN-LAST:event_jButton1ActionPerformed

